package chapter3_Operator;

public class OperatorEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int score = 100;
		
		System.out.println(++score);
		System.out.println(score++);
		System.out.println(score);
		System.out.println(--score);
		System.out.println(score--);
		System.out.println(score);
//		score = score + 1;
//		score += 1;

	}

}
